import { Button } from "@material-ui/core"
import React, { useEffect, useState } from "react"
import { newPiece } from "../misc/Piece"
import GameCanvas from "./GameCanvas"

const Game = (props) => {
	const [gameOver, setGameOver] = useState(true)
	const [grid, setGrid] = useState(null)
	const [currentPiece, setCurrentPiece] = useState(null)
	const [nextPiece, setNextPiece] = useState(null)
	const [context, setContext] = useState(null)

	useEffect(() => {
		// setCurrentPiece(newPiece())
		setNextPiece(newPiece())
	}, [])
	const getNextPiece = () => {
		let next = newPiece
		next.context = context
		next.grid = grid

		let [y, x] = pieceStartPosition(pieceNum, pieceVariant)
		newPiece.x = x
		newPiece.y = y
		newPiece.draw()
		if (newPiece.isCollition(x, y, pieceVariant)) return null
	}

	const handleStart = () => setGameOver(false)

	return (
		<>
			{gameOver ? (
				<Button color="primary" onClick={handleStart}>
					start
				</Button>
			) : (
				<GameCanvas
					setGameOver={setGameOver}
					grid={grid}
					setGrid={setGrid}
					currentPiece={currentPiece}
					setCurrentPiece={setCurrentPiece}
					context={context}
					setContext={setContext}
				/>
			)}
		</>
	)
}

export default Game
