import React from "react";

const GameCanvas = () => {
	const canvasRef = React.useRef(null);
	const [context, setContext] = React.useState(null);

	React.useEffect(() => {
		if (canvasRef.current) {
			const renderCtx = canvasRef.current.getContext("2d");

			if (renderCtx) {
				setContext(renderCtx);
				console.log("useEffect ran with render contaxt", context);
			}
			console.log("use effect ran with canvasRef.current", context);
		}
		console.log("useEffect ran", context);
	}, [context]);

	return (
		<div
			style={{
				textAlign: "center",
			}}
		>
			<canvas
				id="canvas"
				ref={canvasRef}
				width={500}
				height={500}
				style={{
					border: "2px solid #000",
					marginTop: 10,
				}}
			></canvas>
		</div>
	);
};

export default GameCanvas;
