import React, { useEffect, useState, useRef } from "react"
import { config } from "../misc/canvasConfig"
import CanvasGrid from "../misc/canvasGrid"
import controls from "../misc/controls"

const GameCanvas = ({
	grid,
	currentPiece,
	setCurrentPiece,
	setGrid,
	setGameOver,
	context,
	setContext,
	getNextPiece,
}) => {
	const canvasRef = useRef(null)

	useEffect(() => {
		if (canvasRef.current) {
			const renderCtx = canvasRef.current.getContext("2d")

			if (renderCtx) {
				setContext(renderCtx)
			}
		}
	}, [context, setContext])

	useEffect(() => {
		if (context) {
			if (!grid) {
				let gr = new CanvasGrid(context)
				gr.init()
				setGrid(gr)
			} else {
				console.log("grid already exist")

				if (!currentPiece) {
					getNextPiece()
				} else {
					console.log("current piece already set: ", currentPiece)
				}
			}
		}
		return () => {
			console.log("this is where the cleanup stuff happens")
		}
	}, [context, grid, currentPiece, setCurrentPiece, setGrid])

	useEffect(() => {
		const handleKeyPress = ({ key }) => controls(key, currentPiece, grid, context)
		if (currentPiece) {
			document.addEventListener("keydown", handleKeyPress)
		}
		return () => {
			document.removeEventListener("keydown", handleKeyPress)
		}
	}, [currentPiece, grid, context])

	const [seconds, setSeconds] = useState(0)

	useEffect(() => {
		const interval = setInterval(() => {
			setSeconds((seconds) => seconds + 1)
			if (currentPiece)
				if (!currentPiece.moveDown()) {
					currentPiece.lock()
					grid.removeFilledLines()
					getNextPiece()
					setSeconds(0)
					console.log("lock current piece and request for the next piece")
				}
		}, 1000)
		return () => clearInterval(interval)
	}, [currentPiece, context, grid, setGameOver, setCurrentPiece, setContext, getNextPiece])

	return (
		<div
			style={{
				textAlign: "center",
			}}>
			<h3>{seconds} seconds since mount</h3>
			<canvas
				id="canvas"
				ref={canvasRef}
				width={config.COLS * config.BLOCK}
				height={config.ROWS * config.BLOCK}
				style={{
					border: "2px solid #000",
					marginTop: 10,
				}}></canvas>
		</div>
	)
}

export default GameCanvas
